DROP TABLE IF EXISTS `arena_mod`;CREATE TABLE `arena_mod` (`player_guid` int(11) unsigned NOT NULL default '0',`player_team_id` int(10) unsigned NOT NULL default '0',`enemy_team_id` int(10) unsigned NOT NULL default '0',`wins` int(10) unsigned NOT NULL default '0') ENGINE=InnoDB DEFAULT CHARSET=utf8;ALTER TABLE `saved_variables` ADD COLUMN `NextArenaModReset` BIGINT(40) UNSIGNED NOT NULL DEFAULT 0 AFTER `NextArenaPointDistributionTime`;-- Reset saved_variablesTRUNCATE `saved_variables`;